import React from 'react';

function TimelineSlots({ events, selectedDate, onSlotClick }) {
  // Generate hours for the timeline (showing 12-hour format like in your image)
  const hours = Array.from({ length: 24 }, (_, i) => i);
  
  const formatHour = (hour) => {
    if (hour === 0) return '12 AM';
    if (hour < 12) return `${hour} AM`;
    if (hour === 12) return '12 PM';
    return `${hour - 12} PM`;
  };

  const getEventsForHour = (hour) => {
    return events.filter(event => {
      const eventHour = parseInt(event.time.split(':')[0]);
      return eventHour === hour;
    });
  };

  // Color schemes for different time periods
  const getTimeColors = (hour, hasEvents) => {
    if (hasEvents) {
      // Vibrant gradient colors for booked slots based on time of day
      if (hour >= 0 && hour < 6) return { bg: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', border: '#5a67d8', text: 'white' }; // Night - Purple
      if (hour >= 6 && hour < 9) return { bg: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)', border: '#e91e63', text: 'white' }; // Early Morning - Pink
      if (hour >= 9 && hour < 12) return { bg: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)', border: '#2196f3', text: 'white' }; // Late Morning - Blue
      if (hour >= 12 && hour < 15) return { bg: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)', border: '#4caf50', text: 'white' }; // Early Afternoon - Green
      if (hour >= 15 && hour < 18) return { bg: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)', border: '#ff9800', text: 'white' }; // Late Afternoon - Orange
      if (hour >= 18 && hour < 21) return { bg: 'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)', border: '#ff5722', text: '#333' }; // Evening - Sunset
      return { bg: 'linear-gradient(135deg, #d299c2 0%, #fef9d7 100%)', border: '#9c27b0', text: '#333' }; // Late Night - Soft Purple
    } else {
      // Soft gradient colors for available slots
      if (hour >= 0 && hour < 6) return { bg: 'linear-gradient(135deg, #e8eaf6 0%, #f3e5f5 100%)', border: '#b39ddb', text: '#666' }; // Night
      if (hour >= 6 && hour < 9) return { bg: 'linear-gradient(135deg, #fce4ec 0%, #f8bbd9 100%)', border: '#f48fb1', text: '#666' }; // Early Morning
      if (hour >= 9 && hour < 12) return { bg: 'linear-gradient(135deg, #e3f2fd 0%, #bbdefb 100%)', border: '#90caf9', text: '#666' }; // Late Morning
      if (hour >= 12 && hour < 15) return { bg: 'linear-gradient(135deg, #e8f5e8 0%, #c8e6c9 100%)', border: '#a5d6a7', text: '#666' }; // Early Afternoon
      if (hour >= 15 && hour < 18) return { bg: 'linear-gradient(135deg, #fff3e0 0%, #ffcc02 100%)', border: '#ffb74d', text: '#666' }; // Late Afternoon
      if (hour >= 18 && hour < 21) return { bg: 'linear-gradient(135deg, #fce4ec 0%, #f8bbd9 100%)', border: '#f48fb1', text: '#666' }; // Evening
      return { bg: 'linear-gradient(135deg, #f3e5f5 0%, #e1bee7 100%)', border: '#ce93d8', text: '#666' }; // Late Night
    }
  };

  // Show all 24 hours of the day
  const displayHours = hours; // Full day: 12 AM to 11 PM

  return (
    <div style={{ 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      borderRadius: '12px',
      padding: '30px',
      boxShadow: '0 8px 32px rgba(102, 126, 234, 0.3)',
      overflow: 'hidden'
    }}>
      <h3 style={{ 
        marginBottom: '30px', 
        textAlign: 'center',
        fontSize: '28px',
        color: 'white',
        textShadow: '0 2px 4px rgba(0,0,0,0.3)',
        fontWeight: '300'
      }}>
        {selectedDate.toLocaleDateString('en-US', {
          weekday: 'long',
          month: 'long',
          day: 'numeric',
          year: 'numeric'
        })}
      </h3>
      
      {/* Timeline Container */}
      <div style={{ 
        overflowX: 'auto',
        paddingBottom: '20px'
      }}>
        {/* Hour Labels */}
        <div style={{ 
          display: 'flex',
          marginBottom: '20px',
          minWidth: '1200px'
        }}>
          {displayHours.map(hour => (
            <div
              key={`label-${hour}`}
              style={{
                flex: 1,
                textAlign: 'center',
                fontSize: '14px',
                fontWeight: 'bold',
                color: 'white',
                minWidth: '50px',
                padding: '0 2px',
                textShadow: '0 1px 2px rgba(0,0,0,0.3)'
              }}
            >
              {formatHour(hour)}
            </div>
          ))}
        </div>

        {/* Timeline Bar with tick marks */}
        <div style={{
          position: 'relative',
          height: '80px',
          background: 'rgba(255, 255, 255, 0.95)',
          border: '2px solid rgba(255, 255, 255, 0.5)',
          borderRadius: '12px',
          minWidth: '1200px',
          display: 'flex',
          backdropFilter: 'blur(10px)',
          boxShadow: '0 4px 16px rgba(0,0,0,0.2)'
        }}>
          {/* Tick marks */}
          {displayHours.map(hour => {
            const hourEvents = getEventsForHour(hour);
            const hasEvents = hourEvents.length > 0;
            const colors = getTimeColors(hour, hasEvents);
            
            return (
              <div
                key={`slot-${hour}`}
                onClick={() => onSlotClick && onSlotClick(hour)}
                style={{
                  flex: 1,
                  height: '100%',
                  borderRight: hour < displayHours[displayHours.length - 1] ? '1px solid rgba(255,255,255,0.3)' : 'none',
                  cursor: 'pointer',
                  position: 'relative',
                  display: 'flex',
                  flexDirection: 'column',
                  justifyContent: 'center',
                  alignItems: 'center',
                  background: colors.bg,
                  transition: 'all 0.3s ease',
                  minWidth: '40px',
                  borderRadius: '8px',
                  margin: '4px',
                  border: `2px solid ${colors.border}`,
                  transform: hasEvents ? 'scale(1.02)' : 'scale(1)',
                  boxShadow: hasEvents ? '0 4px 12px rgba(0,0,0,0.3)' : '0 2px 8px rgba(0,0,0,0.1)'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.1)';
                  e.target.style.boxShadow = '0 6px 20px rgba(0,0,0,0.4)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = hasEvents ? 'scale(1.02)' : 'scale(1)';
                  e.target.style.boxShadow = hasEvents ? '0 4px 12px rgba(0,0,0,0.3)' : '0 2px 8px rgba(0,0,0,0.1)';
                }}
                title={hasEvents ? 
                  `${hourEvents.length} event(s) at ${formatHour(hour)}` : 
                  `Available at ${formatHour(hour)} - Click to add event`
                }
              >
                {/* Tick mark */}
                <div style={{
                  position: 'absolute',
                  top: '-10px',
                  width: '2px',
                  height: '20px',
                  background: '#666',
                  left: '50%',
                  transform: 'translateX(-50%)'
                }}></div>
                
                {/* Event indicator */}
                {hasEvents && (
                  <div style={{
                    background: 'white',
                    color: '#dc3545',
                    borderRadius: '50%',
                    width: '24px',
                    height: '24px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '12px',
                    fontWeight: 'bold',
                    border: '2px solid white'
                  }}>
                    {hourEvents.length}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      {/* Events Summary */}
      {events.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h4 style={{ marginBottom: '15px', color: '#333' }}>Today's Schedule</h4>
          <div style={{ 
            display: 'grid', 
            gap: '10px',
            maxHeight: '200px',
            overflowY: 'auto'
          }}>
            {events
              .sort((a, b) => a.time.localeCompare(b.time))
              .map(event => (
                <div
                  key={event.id}
                  style={{
                    background: '#f8f9fa',
                    padding: '12px 16px',
                    borderRadius: '6px',
                    border: '1px solid #e0e0e0',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    cursor: 'pointer'
                  }}
                  onClick={() => window.location.href = `/event/${event.id}`}
                >
                  <div>
                    <div style={{ fontWeight: 'bold', color: '#dc3545', marginBottom: '4px' }}>
                      {event.title}
                    </div>
                    <div style={{ fontSize: '14px', color: '#666' }}>
                      {event.time} • {event.duration} min
                    </div>
                  </div>
                  <div style={{
                    background: '#dc3545',
                    color: 'white',
                    padding: '4px 8px',
                    borderRadius: '4px',
                    fontSize: '12px'
                  }}>
                    View
                  </div>
                </div>
              ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default TimelineSlots;
