import React, { useState } from 'react';

function TimelineSlots({ 
  selectedDate, 
  events, 
  todos, 
  onEditEvent, 
  onAddTodo, 
  onToggleTodo, 
  onDeleteTodo, 
  onTimeSlotClick 
}) {
  const [newTodoTexts, setNewTodoTexts] = useState({});

  // Generate hours for the timeline
  const hours = Array.from({ length: 24 }, (_, i) => i);

  // Format hour to 12-hour format
  const formatHour = (hour) => {
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return `${hour12}:00 ${ampm}`;
  };

  // Get events for a specific hour
  const getEventsForHour = (hour) => {
    return events.filter(event => {
      const eventHour = parseInt(event.time.split(':')[0]);
      const eventDuration = event.duration || 60;
      const eventEndHour = eventHour + Math.ceil(eventDuration / 60);
      return hour >= eventHour && hour < eventEndHour;
    });
  };

  // Get todos for a specific hour
  const getTodosForHour = (hour) => {
    return todos.filter(todo => todo.hour === hour);
  };

  // Handle adding new todo
  const handleAddTodo = (hour) => {
    const todoText = newTodoTexts[hour];
    if (todoText && todoText.trim()) {
      onAddTodo(hour, todoText.trim());
      setNewTodoTexts(prev => ({ ...prev, [hour]: '' }));
    }
  };

  // Handle todo text change
  const handleTodoTextChange = (hour, text) => {
    setNewTodoTexts(prev => ({ ...prev, [hour]: text }));
  };

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="text-xl font-semibold text-primary">📋 Daily Timeline</h2>
        <p className="text-sm text-secondary mt-1">Click on time slots to add events or todos</p>
      </div>
      
      <div className="card-body">
        <div className="timeline space-y-2">
          {hours.map(hour => {
            const hourEvents = getEventsForHour(hour);
            const hourTodos = getTodosForHour(hour);
            const hasEvents = hourEvents.length > 0;
            const hasTodos = hourTodos.length > 0;

            return (
              <div 
                key={hour} 
                className={`timeline-item ${hasEvents ? 'has-event' : ''} ${hasTodos ? 'has-todo' : ''}`}
              >
                <div className="flex items-start gap-4 p-4">
                  {/* Time Display */}
                  <div className="timeline-time text-center">
                    <div className="font-semibold text-primary">
                      {formatHour(hour)}
                    </div>
                  </div>

                  {/* Content Area */}
                  <div className="timeline-content flex-1">
                    {/* Events */}
                    {hourEvents.map(event => (
                      <div 
                        key={event.id} 
                        className="event-card cursor-pointer"
                        onClick={() => onEditEvent(event)}
                      >
                        <div className="flex items-center justify-between">
                          <div>
                            <div className="font-semibold">{event.title}</div>
                            {event.description && (
                              <div className="text-sm opacity-90 mt-1">{event.description}</div>
                            )}
                            <div className="text-xs opacity-75 mt-1">
                              {event.time} • {event.duration} minutes
                            </div>
                          </div>
                          <div className="text-lg">✏️</div>
                        </div>
                      </div>
                    ))}

                    {/* Todos */}
                    {hourTodos.map(todo => (
                      <div key={todo.id} className="todo-item">
                        <input
                          type="checkbox"
                          checked={todo.completed}
                          onChange={() => onToggleTodo(todo.id)}
                          className="mr-2"
                        />
                        <span className={todo.completed ? 'line-through opacity-60' : ''}>
                          {todo.text}
                        </span>
                        <button
                          onClick={() => onDeleteTodo(todo.id)}
                          className="ml-auto text-danger hover:text-red-700 text-sm"
                        >
                          🗑️
                        </button>
                      </div>
                    ))}

                    {/* Add Todo Input */}
                    <div className="flex gap-2 mt-2">
                      <input
                        type="text"
                        placeholder="Add a quick todo..."
                        value={newTodoTexts[hour] || ''}
                        onChange={(e) => handleTodoTextChange(hour, e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && handleAddTodo(hour)}
                        className="form-input flex-1 text-sm"
                        style={{ padding: '0.5rem' }}
                      />
                      <button
                        onClick={() => handleAddTodo(hour)}
                        className="btn btn-sm btn-success"
                        disabled={!newTodoTexts[hour]?.trim()}
                      >
                        ➕
                      </button>
                    </div>

                    {/* Add Event Button */}
                    {!hasEvents && (
                      <button
                        onClick={() => onTimeSlotClick(hour)}
                        className="btn btn-sm btn-secondary mt-2"
                      >
                        📅 Add Event
                      </button>
                    )}
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default TimelineSlots;
