import React from 'react';

function DateHeader({ selectedDate, onDateChange }) {
  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleDateChange = (e) => {
    onDateChange(new Date(e.target.value));
  };

  const goToPreviousDay = () => {
    const previousDay = new Date(selectedDate);
    previousDay.setDate(previousDay.getDate() - 1);
    onDateChange(previousDay);
  };

  const goToNextDay = () => {
    const nextDay = new Date(selectedDate);
    nextDay.setDate(nextDay.getDate() + 1);
    onDateChange(nextDay);
  };

  return (
    <div style={{ 
      display: 'flex', 
      alignItems: 'center', 
      justifyContent: 'space-between',
      padding: '25px',
      background: 'linear-gradient(135deg, #fefefe 0%, #f8fafc 30%, #f1f5f9 70%, #fefefe 100%)',
      borderRadius: '20px',
      marginBottom: '20px',
      boxShadow: '0 10px 40px rgba(148, 163, 184, 0.08), 0 4px 20px rgba(203, 213, 225, 0.05)',
      border: '1px solid rgba(203, 213, 225, 0.2)',
      backdropFilter: 'blur(25px)'
    }}>
      <button 
        className="btn btn-secondary"
        onClick={goToPreviousDay}
        style={{
          background: 'linear-gradient(135deg, #e0e7ff 0%, #c7d2fe 100%)',
          border: '2px solid #a5b4fc',
          color: '#4338ca',
          padding: '12px 20px',
          borderRadius: '12px',
          fontWeight: 'bold',
          boxShadow: '0 4px 16px rgba(165, 180, 252, 0.3)',
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'translateY(-2px) scale(1.05)';
          e.target.style.boxShadow = '0 8px 24px rgba(165, 180, 252, 0.4)';
          e.target.style.background = 'linear-gradient(135deg, #c7d2fe 0%, #a5b4fc 100%)';
          e.target.style.color = '#3730a3';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'translateY(0) scale(1)';
          e.target.style.boxShadow = '0 4px 16px rgba(165, 180, 252, 0.3)';
          e.target.style.background = 'linear-gradient(135deg, #e0e7ff 0%, #c7d2fe 100%)';
          e.target.style.color = '#4338ca';
        }}
      >
        ‚Üê Previous
      </button>
      
      <div style={{ textAlign: 'center' }}>
        <h2 style={{ 
          marginBottom: '15px', 
          color: 'white',
          textShadow: '0 2px 4px rgba(0,0,0,0.3)',
          fontSize: '24px',
          fontWeight: '300'
        }}>
          {formatDate(selectedDate)}
        </h2>
        <input
          type="date"
          value={selectedDate.toISOString().split('T')[0]}
          onChange={handleDateChange}
          style={{
            padding: '12px 16px',
            border: '2px solid rgba(255, 255, 255, 0.3)',
            borderRadius: '10px',
            fontSize: '16px',
            background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.85) 100%)',
            color: '#333',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1), 0 0 20px rgba(255, 255, 255, 0.2)',
            transition: 'all 0.3s ease',
            cursor: 'pointer',
            backdropFilter: 'blur(10px)',
            minWidth: '180px'
          }}
          onFocus={(e) => {
            e.target.style.border = '2px solid #43e97b';
            e.target.style.boxShadow = '0 0 25px rgba(67, 233, 123, 0.5), 0 4px 20px rgba(0, 0, 0, 0.2)';
            e.target.style.transform = 'scale(1.05)';
            e.target.style.background = 'linear-gradient(135deg, rgba(255, 255, 255, 1) 0%, rgba(67, 233, 123, 0.1) 100%)';
          }}
          onBlur={(e) => {
            e.target.style.border = '2px solid rgba(255, 255, 255, 0.3)';
            e.target.style.boxShadow = '0 4px 15px rgba(0, 0, 0, 0.1), 0 0 20px rgba(255, 255, 255, 0.2)';
            e.target.style.transform = 'scale(1)';
            e.target.style.background = 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.85) 100%)';
          }}
        />
      </div>
      
      <button 
        className="btn btn-secondary"
        onClick={goToNextDay}
        style={{
          background: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
          border: '2px solid rgba(255, 255, 255, 0.3)',
          color: 'white',
          padding: '12px 20px',
          borderRadius: '8px',
          fontWeight: 'bold',
          boxShadow: '0 4px 15px rgba(67, 233, 123, 0.4)',
          transition: 'all 0.3s ease'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'translateY(-2px)';
          e.target.style.boxShadow = '0 6px 20px rgba(67, 233, 123, 0.6)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'translateY(0)';
          e.target.style.boxShadow = '0 4px 15px rgba(67, 233, 123, 0.4)';
        }}
      >
        Next ‚Üí
      </button>
    </div>
  );
}

export default DateHeader;
