import React, { useState } from 'react';

function TimelineSlots({ 
  selectedDate, 
  events, 
  todos, 
  onEditEvent, 
  onToggleEventCompletion,
  onAddTodo, 
  onToggleTodo, 
  onDeleteTodo, 
  onTimeSlotClick 
}) {
  // Generate hours for display (showing full day)
  const hours = Array.from({ length: 24 }, (_, i) => i); // 12 AM to 11 PM

  // Format hour to 12-hour format
  const formatHour = (hour) => {
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return `${hour12} ${ampm}`;
  };

  // Format time with minutes
  const formatTimeWithMinutes = (hour, minute) => {
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return `${hour12}:${minute.toString().padStart(2, '0')} ${ampm}`;
  };

  // Get events for a specific hour
  const getEventsForHour = (hour) => {
    return events.filter(event => {
      const eventHour = parseInt(event.time.split(':')[0]);
      const eventDuration = event.duration || 60;
      const eventEndHour = eventHour + Math.ceil(eventDuration / 60);
      return hour >= eventHour && hour < eventEndHour;
    });
  };

  // Get todos for a specific hour
  const getTodosForHour = (hour) => {
    return todos.filter(todo => {
      const todoDate = selectedDate.toISOString().split('T')[0];
      return todo.hour === hour && todo.date === todoDate;
    });
  };

  // Check if any event in this hour is completed
  const hasCompletedEventForHour = (hour) => {
    const hourEvents = getEventsForHour(hour);
    return hourEvents.some(event => event.completed);
  };

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="text-xl font-semibold text-primary">📋 Daily Timeline</h2>
        <p className="text-sm text-secondary mt-1">Select time range and manage your schedule</p>
      </div>
      
      <div className="card-body">
        {/* Horizontal Timeline */}
        <div className="horizontal-timeline mb-6">
          {/* Time Labels */}
          <div className="timeline-hours flex justify-between items-center mb-4 px-4">
            {hours.map(hour => (
              <div key={hour} className="timeline-hour text-center">
                <div className="text-xs font-semibold text-primary mb-2">
                  {formatHour(hour)}
                </div>
              </div>
            ))}
          </div>

          {/* Timeline Slot Boxes */}
          <div className="timeline-slots grid grid-cols-24 gap-1 mb-6 px-4">
            {hours.map(hour => {
              const hasEvents = getEventsForHour(hour).length > 0;
              const hasCompletedEvent = hasCompletedEventForHour(hour);
              const hourTodos = getTodosForHour(hour);
              const completedTodos = hourTodos.filter(todo => todo.completed);
              const pendingTodos = hourTodos.filter(todo => !todo.completed);
              
              return (
                <div key={hour} className="relative">
                  <div
                    className={`timeline-slot-box h-16 rounded-md cursor-pointer border-2 transition-all duration-200 ${
                      hasEvents 
                        ? 'bg-blue-500 border-blue-500 text-white shadow-md' 
                        : 'bg-gray-200 border-gray-300 hover:border-gray-400 hover:bg-gray-300'
                    }`}
                    onClick={() => onTimeSlotClick(hour)}
                    title={`${formatHour(hour)} - ${hasEvents ? 'Event scheduled' : 'Click to add event'}`}
                  >
                    <div className="h-full flex flex-col items-center justify-center p-1">
                      {/* No content needed - just blue color for events */}
                    </div>
                  </div>
                  
                  {/* Todo Box for Completed Events */}
                  {hasCompletedEvent && hourTodos.length > 0 && (
                    <div className="absolute top-20 left-0 w-full z-10">
                      <div className="bg-white border-2 border-success rounded-lg shadow-lg p-3 text-xs">
                        <div className="font-semibold text-success mb-2 flex items-center gap-1">
                          ✅ {formatHour(hour)} Todos
                        </div>
                        
                        {completedTodos.length > 0 && (
                          <div className="mb-2">
                            <div className="text-success font-medium mb-1">✓ Done ({completedTodos.length})</div>
                            <div className="space-y-1">
                              {completedTodos.map(todo => (
                                <div key={todo.id} className="text-gray-600 line-through text-xs">
                                  • {todo.text}
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                        
                        {pendingTodos.length > 0 && (
                          <div>
                            <div className="text-warning font-medium mb-1">⏳ Pending ({pendingTodos.length})</div>
                            <div className="space-y-1">
                              {pendingTodos.map(todo => (
                                <div key={todo.id} className="text-gray-800 text-xs">
                                  • {todo.text}
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>

        {/* All Events */}
        <div className="events-list">
          <h3 className="text-lg font-semibold text-primary mb-4">
            📅 Today's Events
          </h3>
          
          <div className="space-y-3">
            {events.map(event => (
              <div 
                key={event.id} 
                className={`event-card ${event.completed ? 'opacity-75 bg-gradient-to-r from-green-50 to-green-100' : ''}`}
              >
                <div className="flex items-center gap-3">
                  {/* Checkbox for completion */}
                  <input
                    type="checkbox"
                    checked={event.completed || false}
                    onChange={(e) => {
                      e.stopPropagation();
                      onToggleEventCompletion(event.id);
                    }}
                    className="w-5 h-5 text-success bg-white border-2 border-white rounded focus:ring-success focus:ring-2 cursor-pointer"
                  />
                  
                  {/* Event content */}
                  <div 
                    className="flex-1 cursor-pointer"
                    onClick={() => onEditEvent(event)}
                  >
                    <div className="flex items-center justify-between">
                      <div>
                        <div className="font-semibold flex items-center gap-2">
                          {event.completed && <span className="text-green-300">✅</span>}
                          <span className={event.completed ? 'line-through' : ''}>{event.title}</span>
                        </div>
                        {event.description && (
                          <div className="text-sm opacity-90 mt-1">{event.description}</div>
                        )}
                        <div className="text-xs opacity-75 mt-1">
                          {event.time} • {event.duration} minutes
                          {event.completed && <span className="ml-2 text-green-300 font-medium">• Completed</span>}
                        </div>
                      </div>
                      <div className="text-lg">✏️</div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
            
            {events.length === 0 && (
              <div className="text-center py-8 text-secondary">
                <div className="text-4xl mb-2">📅</div>
                <div>No events scheduled for today</div>
                <button 
                  onClick={() => onTimeSlotClick(9)}
                  className="btn btn-sm btn-primary mt-3"
                >
                  Add Event
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TimelineSlots;
