import React, { useState } from 'react';
import { motion } from 'framer-motion';
import DateHeader from '../components/DateHeader';
import TimelineSlots from '../components/TimelineSlots';
import AddEventModal from '../components/AddEventModal';
import TodoModal from '../components/TodoModal';

function SchedulePage() {
  const [events, setEvents] = useState([]);
  const [todos, setTodos] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showTodoModal, setShowTodoModal] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [editingEvent, setEditingEvent] = useState(null);
  const [selectedHour, setSelectedHour] = useState(null);
  const [todoTimeSlot, setTodoTimeSlot] = useState(null);

  const addEvent = (eventData) => {
    const newEvent = {
      id: Date.now(),
      ...eventData,
      date: selectedDate.toISOString().split('T')[0]
    };
    setEvents([...events, newEvent]);
    setShowModal(false);
    setSelectedHour(null);
  };

  const updateEvent = (updatedEvent) => {
    setEvents(events.map(event => 
      event.id === updatedEvent.id ? updatedEvent : event
    ));
    setShowModal(false);
    setEditingEvent(null);
    setSelectedHour(null);
  };

  const deleteEvent = (eventId) => {
    setEvents(events.filter(event => event.id !== eventId));
    setShowModal(false);
    setEditingEvent(null);
    setSelectedHour(null);
  };

  const handleSlotClick = (hour) => {
    setEditingEvent(null);
    setSelectedHour(hour);
    setShowModal(true);
  };

  const handleEventEdit = (event) => {
    setEditingEvent(event);
    setSelectedHour(null);
    setShowModal(true);
  };

  const handleModalClose = () => {
    setShowModal(false);
    setEditingEvent(null);
    setSelectedHour(null);
  };

  const handleTodoClick = (hour) => {
    setTodoTimeSlot(hour);
    setShowTodoModal(true);
  };

  const handleTodoModalClose = () => {
    setShowTodoModal(false);
    setTodoTimeSlot(null);
  };

  const addTodo = (todoData) => {
    setTodos([...todos, todoData]);
  };

  const toggleTodo = (todoId) => {
    setTodos(todos.map(todo =>
      todo.id === todoId ? { ...todo, completed: !todo.completed } : todo
    ));
  };

  const deleteTodo = (todoId) => {
    setTodos(todos.filter(todo => todo.id !== todoId));
  };

  // Filter events for the selected date
  const todaysEvents = events.filter(event => 
    event.date === selectedDate.toISOString().split('T')[0]
  );

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      transition={{ duration: 0.5, ease: "easeOut" }}
      className="container"
    >
      <div style={{ padding: '20px' }}>
        <DateHeader 
          selectedDate={selectedDate}
          onDateChange={setSelectedDate}
        />
        
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <h2 style={{ margin: 0, color: '#333' }}>Schedule Timeline</h2>
          <button 
            className="btn btn-primary"
            onClick={() => {
              setEditingEvent(null);
              setSelectedHour(null);
              setShowModal(true);
            }}
          >
            + Add Event
          </button>
        </div>

        <TimelineSlots
          events={todaysEvents}
          selectedDate={selectedDate}
          onSlotClick={handleSlotClick}
          onEventEdit={handleEventEdit}
        />

        {showModal && (
          <AddEventModal
            onClose={handleModalClose}
            onAddEvent={addEvent}
            onUpdateEvent={updateEvent}
            onDeleteEvent={deleteEvent}
            selectedDate={selectedDate}
            editingEvent={editingEvent}
            selectedHour={selectedHour}
          />
        )}
      </div>
    </motion.div>
  );
}

export default SchedulePage;
