import React, { useState } from 'react';
import { motion } from 'framer-motion';
import DateHeader from '../components/DateHeader';
import Timeline from '../components/Timeline';
import TimeRangeSelector from '../components/TimeRangeSelector';
import AddEventModal from '../components/AddEventModal';

function SchedulePage() {
  const [events, setEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [timeRange, setTimeRange] = useState({ from: '09:00', to: '17:00' });

  const addEvent = (eventData) => {
    const newEvent = {
      id: Date.now(),
      ...eventData,
      date: selectedDate.toISOString().split('T')[0]
    };
    setEvents([...events, newEvent]);
    setShowModal(false);
  };

  const handleTimeRangeChange = (fromTime, toTime) => {
    setTimeRange({ from: fromTime, to: toTime });
  };

  const handleTimeSlotClick = (hour) => {
    const timeString = `${hour.toString().padStart(2, '0')}:00`;
    setShowModal(true);
  };

  // Filter events for the selected date
  const todaysEvents = events.filter(event => 
    event.date === selectedDate.toISOString().split('T')[0]
  );

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="container"
    >
      <div style={{ padding: '20px' }}>
        <DateHeader 
          selectedDate={selectedDate}
          onDateChange={setSelectedDate}
        />
        
        <TimeRangeSelector
          onTimeRangeChange={handleTimeRangeChange}
          selectedFromTime={timeRange.from}
          selectedToTime={timeRange.to}
        />

        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '20px'
        }}>
          <h2>Schedule Overview</h2>
          <button 
            className="btn btn-primary"
            onClick={() => setShowModal(true)}
          >
            + Add Event
          </button>
        </div>

        <Timeline
          events={todaysEvents}
          selectedDate={selectedDate}
          onTimeSlotClick={handleTimeSlotClick}
        />

        {showModal && (
          <AddEventModal
            onClose={() => setShowModal(false)}
            onAddEvent={addEvent}
            selectedDate={selectedDate}
          />
        )}
      </div>
    </motion.div>
  );
}

export default SchedulePage;
