import React, { useState } from 'react';

function TimelineSlots({ 
  selectedDate, 
  events, 
  todos, 
  onEditEvent, 
  onAddTodo, 
  onToggleTodo, 
  onDeleteTodo, 
  onTimeSlotClick 
}) {
  const [selectedFromHour, setSelectedFromHour] = useState(9); // Default 9 AM
  const [selectedToHour, setSelectedToHour] = useState(17); // Default 5 PM
  const [selectedFromMinute, setSelectedFromMinute] = useState(0);
  const [selectedToMinute, setSelectedToMinute] = useState(0);

  // Generate hours for display (showing work hours primarily)
  const hours = Array.from({ length: 12 }, (_, i) => i + 6); // 6 AM to 5 PM

  // Format hour to 12-hour format
  const formatHour = (hour) => {
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return `${hour12} ${ampm}`;
  };

  // Format time with minutes
  const formatTimeWithMinutes = (hour, minute) => {
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const hour12 = hour % 12 || 12;
    return `${hour12}:${minute.toString().padStart(2, '0')} ${ampm}`;
  };

  // Get events for a specific hour
  const getEventsForHour = (hour) => {
    return events.filter(event => {
      const eventHour = parseInt(event.time.split(':')[0]);
      const eventDuration = event.duration || 60;
      const eventEndHour = eventHour + Math.ceil(eventDuration / 60);
      return hour >= eventHour && hour < eventEndHour;
    });
  };

  // Check if an hour is within selected range
  const isHourInRange = (hour) => {
    const fromTime = selectedFromHour + selectedFromMinute / 60;
    const toTime = selectedToHour + selectedToMinute / 60;
    return hour >= fromTime && hour < toTime;
  };

  return (
    <div className="card">
      <div className="card-header">
        <h2 className="text-xl font-semibold text-primary">📋 Daily Timeline</h2>
        <p className="text-sm text-secondary mt-1">Select time range and manage your schedule</p>
      </div>
      
      <div className="card-body">
        {/* Horizontal Timeline */}
        <div className="horizontal-timeline mb-6">
          {/* Time Labels */}
          <div className="timeline-hours flex justify-between items-center mb-4 px-4">
            {hours.map(hour => (
              <div key={hour} className="timeline-hour text-center">
                <div className="text-sm font-semibold text-primary mb-2">
                  {formatHour(hour)}
                </div>
                <div className="timeline-tick w-1 h-6 bg-primary-300 mx-auto"></div>
              </div>
            ))}
          </div>

          {/* Timeline Bar */}
          <div className="timeline-bar relative bg-primary-100 h-12 rounded-lg mb-6 mx-4">
            {/* Hour markers */}
            {hours.map(hour => {
              const position = ((hour - 6) / 11) * 100; // 11 hours total (6 AM to 5 PM)
              const hasEvents = getEventsForHour(hour).length > 0;
              const inRange = isHourInRange(hour);
              
              return (
                <div
                  key={hour}
                  className={`absolute top-0 bottom-0 w-2 cursor-pointer ${
                    hasEvents ? 'bg-accent-blue' : 
                    inRange ? 'bg-accent-green opacity-60' : 'bg-primary-200'
                  } hover:bg-accent-blue transition-colors`}
                  style={{ left: `${position}%` }}
                  onClick={() => onTimeSlotClick(hour)}
                  title={`${formatHour(hour)} - Click to add event`}
                ></div>
              );
            })}

            {/* Selected range indicator */}
            <div
              className="absolute top-1 bottom-1 bg-accent-green rounded opacity-40"
              style={{
                left: `${((selectedFromHour - 6) / 11) * 100}%`,
                width: `${((selectedToHour - selectedFromHour) / 11) * 100}%`
              }}
            ></div>
          </div>

          {/* Time Range Selectors */}
          <div className="grid grid-cols-2 gap-6 max-w-md mx-auto">
            {/* From Time */}
            <div className="text-center">
              <div className="text-success font-semibold mb-3">From</div>
              <div className="time-selector bg-white border-2 border-primary-200 rounded-lg p-4">
                <div className="flex items-center justify-center gap-2 mb-2">
                  <select
                    value={selectedFromHour}
                    onChange={(e) => setSelectedFromHour(parseInt(e.target.value))}
                    className="form-select text-center text-2xl font-bold w-16"
                  >
                    {Array.from({ length: 24 }, (_, i) => (
                      <option key={i} value={i}>
                        {i.toString().padStart(2, '0')}
                      </option>
                    ))}
                  </select>
                  <span className="text-2xl font-bold">:</span>
                  <select
                    value={selectedFromMinute}
                    onChange={(e) => setSelectedFromMinute(parseInt(e.target.value))}
                    className="form-select text-center text-2xl font-bold w-16"
                  >
                    <option value={0}>00</option>
                    <option value={15}>15</option>
                    <option value={30}>30</option>
                    <option value={45}>45</option>
                  </select>
                </div>
                <div className="text-sm text-secondary">
                  {formatTimeWithMinutes(selectedFromHour, selectedFromMinute)}
                </div>
              </div>
            </div>

            {/* To Time */}
            <div className="text-center">
              <div className="text-success font-semibold mb-3">To</div>
              <div className="time-selector bg-white border-2 border-primary-200 rounded-lg p-4">
                <div className="flex items-center justify-center gap-2 mb-2">
                  <select
                    value={selectedToHour}
                    onChange={(e) => setSelectedToHour(parseInt(e.target.value))}
                    className="form-select text-center text-2xl font-bold w-16"
                  >
                    {Array.from({ length: 24 }, (_, i) => (
                      <option key={i} value={i}>
                        {i.toString().padStart(2, '0')}
                      </option>
                    ))}
                  </select>
                  <span className="text-2xl font-bold">:</span>
                  <select
                    value={selectedToMinute}
                    onChange={(e) => setSelectedToMinute(parseInt(e.target.value))}
                    className="form-select text-center text-2xl font-bold w-16"
                  >
                    <option value={0}>00</option>
                    <option value={15}>15</option>
                    <option value={30}>30</option>
                    <option value={45}>45</option>
                  </select>
                </div>
                <div className="text-sm text-secondary">
                  {formatTimeWithMinutes(selectedToHour, selectedToMinute)}
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Events in Selected Range */}
        <div className="events-list">
          <h3 className="text-lg font-semibold text-primary mb-4">
            📅 Events ({formatTimeWithMinutes(selectedFromHour, selectedFromMinute)} - {formatTimeWithMinutes(selectedToHour, selectedToMinute)})
          </h3>
          
          <div className="space-y-3">
            {events
              .filter(event => {
                const eventHour = parseInt(event.time.split(':')[0]);
                return eventHour >= selectedFromHour && eventHour <= selectedToHour;
              })
              .map(event => (
                <div 
                  key={event.id} 
                  className="event-card cursor-pointer"
                  onClick={() => onEditEvent(event)}
                >
                  <div className="flex items-center justify-between">
                    <div>
                      <div className="font-semibold">{event.title}</div>
                      {event.description && (
                        <div className="text-sm opacity-90 mt-1">{event.description}</div>
                      )}
                      <div className="text-xs opacity-75 mt-1">
                        {event.time} • {event.duration} minutes
                      </div>
                    </div>
                    <div className="text-lg">✏️</div>
                  </div>
                </div>
              ))}
            
            {events.filter(event => {
              const eventHour = parseInt(event.time.split(':')[0]);
              return eventHour >= selectedFromHour && eventHour <= selectedToHour;
            }).length === 0 && (
              <div className="text-center py-8 text-secondary">
                <div className="text-4xl mb-2">📅</div>
                <div>No events in selected time range</div>
                <button 
                  onClick={() => onTimeSlotClick(selectedFromHour)}
                  className="btn btn-sm btn-primary mt-3"
                >
                  Add Event
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default TimelineSlots;
